mapper.update:
UPDATE USER
SET user_name = #{userName}, user_password = #{userPassword}, user_type = #{userType}
WHERE user_email = #{userEmail}

mapper.select:
SELECT user_id, user_name, user_password, user_email, user_type
FROM user
WHERE user_email = #{userEmail}

SELECT user_id, user_name, user_password, user_email, user_type
FROM user
WHERE user_type = #{userType}
